#!/bin/bash
set -e
script_dir=$( cd "$( dirname "${BASH_SOURCE[0]}" )" && pwd )
. $script_dir/setup-env.sh

echo "Cleaning up..."

rm -rf nativescript.tgz || true
rm -rf tns-core-modules*.tgz || true
rm -rf tns-definitions*.tgz || true
rm -rf tns-android*.tgz || true
rm -rf tns-ios*.tgz || true
rm -rf npm-debug.log || true
rm -rf tns-core-modules-widgets.tgz || true

if [ -d $telerik_com_resources_mount ]; then
    echo "Trying to unmount $telerik_com_distributions_mount..."
	umount -f $telerik_com_distributions_mount
	rm -rd $telerik_com_distributions_mount
	echo "Unmounted and deleted $telerik_com_distributions_mount"
fi
