#!/bin/bash
set -e
script_dir=$( cd "$( dirname "${BASH_SOURCE[0]}" )" && pwd )
. $script_dir/setup-env.sh

echo "Downloading latest Android Widgets..."

archive=K:/DailyBuilds/NativeScript/android-widgets/Stable/tns-core-modules-widgets.tgz

if [ -f $archive ]; then
	cp $archive .
else
	archive=$telerik_com_distributions_mount/DailyBuilds/NativeScript/android-widgets/Stable/tns-core-modules-widgets.tgz
	if [ ! -f $archive ]; then
		$script_dir/mount-distributions.sh
	fi

	cp $archive .
	echo "Copied $archive"
fi
