#!/bin/bash
set -e
script_dir=$( cd "$( dirname "${BASH_SOURCE[0]}" )" && pwd )
. $script_dir/setup-env.sh

platform=${1:-android}

echo "Downloading latest $platform runtime..."

archive=K:/DailyBuilds/NativeScript/tns-$platform/Stable/tns-$platform.tgz

if [ -f $archive ]; then
	cp $archive .
else
	archive=$telerik_com_distributions_mount/DailyBuilds/NativeScript/tns-$platform/Stable/tns-$platform.tgz
	if [ ! -f $archive ]; then
		$script_dir/mount-distributions.sh
	fi

	cp $archive .
	echo "Copied $archive"
fi
