#!/bin/bash
set -e
script_dir=$( cd "$( dirname "${BASH_SOURCE[0]}" )" && pwd )

name=${1:-NativeScript}
platform=${2:-android}

echo "Setting up platform $platform..."
$script_dir/download-latest-runtime.sh $platform
$script_dir/add-latest-runtime.sh $name $platform
$script_dir/build-project.sh $name $platform
$script_dir/prepare-project.sh $name $platform
echo "Platform $platform is ready."
